    private void btnIniciarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnIniciarActionPerformed
        // bOTON iNICIAR
        en.setNomUsuario(txtusuario.getText());
        en.clave(txtclave.getText());
        if (dao.AccesoSistema(en)==true) {
            JOptionPane.showMessageDialog(null,"Bienvenidos al sistema"+ txtusuario.getText(),"SISTEMAS", JOptionPane.INFORMATION_MESSAGE);
            PrincipalMDI p=new PrincipalMDI();
            p.show();
            this.dispose();
        }else{
            JOptionPane.showConfirmDialog(null,"Datos del usuario incorrecto", "SISTEMAS", JOptionPane.ERROR_MESSAGE);
        }
        
    }//GEN-LAST:event_btnIniciarActionPerformed

